@import url("https://fonts.googleapis.com/css?family=Cabin");
@font-face {
  font-family: "Charter";
  src: url("b0be953c13772ccb9e65bc1d9900c9f5.woff") format("woff");
  font-weight: normal;
  font-style: normal;
}
@font-face {
  font-family: "Charter";
  src: url("328a04064c78b2773bc6baab671ffdaa.woff") format("woff");
  font-weight: normal;
  font-style: italic;
}
@font-face {
  font-family: "Charter";
  src: url("ef56b10ac93a3c856ac7bf02d172d3d9.woff") format("woff");
  font-weight: bold;
  font-style: normal;
}
body {
  background: #444;
  margin: 0;
  width: 100%;
  min-height: 100vh;
}

h1, p, a {
  color: #fff;
}

h1 {
  font-family: "Charter", serif;
  font-weight: bold;
}

p {
  font-family: "Charter", serif;
  letter-spacing: -0.04em;
  margin-top: 0;
}

.me-container {
  height: 100vh;
  width: 100%;
  display: grid;
  align-items: center;
  grid-template-columns: 1fr 1fr;
  grid-template-rows: 1fr 1fr 1fr;
  grid-template-areas: "wkma ." "me work" "touch .";
  overflow: hidden;
}

.me {
  grid-area: me;
  display: grid;
  grid-template-columns: auto minmax(672px, 750px) auto;
}
.me h1 {
  font-size: 5.063em;
  letter-spacing: 0.08em;
  margin: 0;
}
.me p {
  color: #999;
  font-size: 3.375em;
}
.me span {
  color: #999;
  text-decoration: underline;
  transition: all 1s;
}

.me:hover span {
  color: palevioletred;
}

.case {
  grid-column: 2;
}

.touch, .work, .wkma {
  display: flex;
  height: 100%;
  width: 100%;
}
.touch p, .work p, .wkma p {
  display: inline-block;
  color: #999;
  font-size: 2.25em;
  transition: all 0.5s;
}
.touch span, .work span, .wkma span {
  display: inline-block;
  transform: translateY(2px);
}

.touch {
  text-align: center;
  grid-area: touch;
}
.touch p {
  transform: rotate(90deg);
}

.wkma {
  grid-area: wkma;
}
.work {
  grid-area: work;
}

.work:hover p, .wkma:hover p {
  font-size: 5.063em;
  color: palevioletred;
}

.touch:hover p {
  font-size: 5.063em;
  color: palevioletred;
  transform: rotate(0);
}
