  font-family: "Charter", serif;
  letter-spacing: -0.04em;
  margin-top: 0;
}

a {
  text-decoration: none;
}

span {
  text-decoration: underline;
}

.me-container {
  height: 100vh;
  width: 100%;
  display: grid;
  align-items: center;
  grid-template-columns: 1fr 1fr;
  grid-template-rows: 1fr 1fr 1fr;
  grid-template-areas: "wkma ." "me projects" "touch .";
}

.me {
  grid-area: me;
  display: grid;
  grid-template-columns: auto minmax(450px, 750px) auto;
}
